INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (157, '哆啦A梦：伴我同行2', 1, '2021-05-28 08:00:00', 120, '时隔6年，动画电影“伴我同行”系列回归，同时本片也是该系列的终章。大雄为了实现奶奶看到妻子的愿望，想坐着时光机去未来参加他的婚礼，但爱情长跑50年终于要娶到静香的大雄竟然想逃婚…', 9.2, 106000000, '日本', '剧情／动画', 'https://p0.meituan.net/movie/69291d617c3dc5dcac5e3bf29dda63ac2200192.jpg@218w_300h_1e_1c', 1234000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (158, '1921', 2, '2021-07-01 08:00:00', 120, '1921年的上海，租界林立，局势剑拔弩张。来自五湖四海平均年龄仅28岁的热血青年们，突破国际各股复杂势力的监控和追踪，在上海共聚，创立中国共产党。从此中国历史进入了新篇章。', 0, 1312310, '中国大陆', '剧情／历史', 'https://p1.meituan.net/movie/63cb3dbbaff624b236b2e22b2ee59cff1553093.jpg@218w_300h_1e_1c', 345000, 177176);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (159, '再见，少年', 2, '2021-07-01 08:00:00', 120, '影片讲述了千禧年发生在南方小镇上的一段曾经无限接近，却又渐行渐远的少年情谊。“好学生”黎菲与“坏孩子”张辰浩，各自经历了时代大潮下家庭的变迁，一起奋力而坚韧地成长。然而高考前的一场剧变，让青春笼上阴影，最终裹挟住了二人的命运。', 0, 56757567, '中国大陆', '青春／剧情', 'https://p0.meituan.net/movie/212eb488c085368630ab12fd104f52512585862.jpg@218w_300h_1e_1c', 896660, 115788);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (160, '人之怒', 1, '2021-05-10 08:00:00', 120, '在一次全副武装的运钞车劫案中，梅斯先生（杰森·斯坦森饰）的儿子作为案发目击者被劫匪杀害。怒火中烧的梅斯为报丧子之仇，化身H卧底成为运钞车安保，试图找出杀害他儿子的劫匪以及与劫匪暗中勾结的安保公司内鬼。', 0, 157000000, '中国大陆', '动作', 'https://p0.meituan.net/moviemachine/e1178cc2abab5938c2113876da248fe0286888.jpg@218w_300h_1e_1c', 756767, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (161, '古董局中局2', 2, '2021-06-09 23:46:54', 135, '来自日本的木户小姐（松峰莉璃 饰）即将向中国归还一尊唐代武则天明堂佛头，却指明要文物界权威组织“五脉”旗下白字门许家后人出面接收。但五脉白字门早已没落，许家后人也不知所踪。情势紧张，五脉掌门黄克武的孙女黄烟烟（辛芷蕾 饰）却表示，自己有办法找到许家后人的踪迹。而当初背负汉奸骂名，将佛头送去日本的白字门家主许一城之孙——许愿（雷佳音 饰），有一身鉴古天赋的他，如今只是一个胸无大志的电器铺小老板，靠倒卖电器勉强维持生计。许愿幼年时因为一场火灾失去了母亲，而父亲许和平在火灾之后便对他保持着疏离的态度，不仅如此，他还要承受着外界对自己家族的骂名——时至今日，在黄烟烟的劝说下，他不情愿的前往与五脉交涉，却敏锐地察觉到佛头是赝品的事实。', 0, 6575757, '中国大陆', '剧情、逻辑', 'https://p0.meituan.net/movie/901384f7dfef377705548c26bcec455c2185870.jpg@218w_300h_1e_1c', 12009, 167398);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (162, '我要我们在一起', 1, '2021-05-20 08:00:00', 120, '你有没有爱过一个人，曾经拼了命，只为和TA在一起。十年前，差生吕钦扬当众告白凌一尧，两人从校园步入社会，为了让她幸福，他不惜以命相搏。然而金钱、房子、婚姻等现实的考验，却将两人越推越远。十年长跑，他们能否还记得曾经刻在心底的约定：我要我们在一起。电影改编自长帖《与我十年长跑的女友明天要嫁人了》。', 8, 272000000, '中国大陆', '爱情', 'https://p0.meituan.net/movie/dc2fed6001e809e4553f90cc6fad9a59245170.jpg@218w_300h_1e_1c', 5258000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (163, '四海', 2, '2022-02-01 08:00:00', 120, '擅长摩托车特技的少年吴仁耀（刘昊然 饰）与他多年未见的父亲吴仁腾（沈腾 饰）再度相逢，此时女孩欢颂（刘浩存 饰）和她的哥哥的车队又意外闯入他们的生活，少年阿耀究竟收获的是朋友还是敌人，是快乐还是烦恼？他们终将名扬四海还是四海为家？北回归线上的小海岛与南方最繁华大城市的公路之旅，燃烧着他们的成长与青春。', 0, 123400, '中国大陆', '喜剧／青春／动作', 'https://p0.meituan.net/movie/923aa8ebb795888b550b4951124682642023435.jpg@218w_300h_1e_1c', 20098, 17216);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (164, '指环王：王者无敌', 1, '2021-05-14 08:00:00', 120, '魔改编自J • R • R •托尔金的史诗巨著，在三部曲终结篇《指环王：王者无敌》中，各方势力集结，决定中土世界命运的最终决战也拉开了帷幕。光明与黑暗之间进行着激烈对决，护戒联盟的成员阿拉贡也直面身份，肩负起人皇继承人的担当与责任。然而，战胜邪恶的唯一希望仍然紧系在霍比特人弗罗多身上。为完成熔毁至尊戒这项几乎不可能完成的任务，弗罗多在忠诚的朋友山姆和可恶又可怜的咕噜的陪伴下，冒险深入黑暗之地魔多。', 9.5, 119000000, '中国大陆', '剧情／动作／奇幻／冒险', 'https://p0.meituan.net/moviemachine/b183a9143031dd4aab5ca95affe410f2579878.jpg@218w_300h_1e_1c', 43535435, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (165, '热带往事', 2, '2021-06-18 08:00:00', 120, '南方炎热的夏日夜晚，年轻的王学明（彭于晏饰）遭遇了一起意外。梁妈（张艾嘉饰）的老公 忽然失踪，她寻求陈警官的帮助，却不知自己已成为别人的下一个“目标”。陈警官（王砚辉饰）负责老梁 失踪案，他发现案情的发展远超自己掌控。这群不知命运已被悄然更改的人，都在试图窥视关于那个夜晚的真相，但这个夏夜的记忆如错乱的拼图，愈加扑朔迷离...', 0, 1213100, '中国大陆', '剧情／犯罪／悬疑', 'https://p0.meituan.net/moviemachine/8db13be80574cfae05626d6df8f84c09122704.jpg@218w_300h_1e_1c', 23098, 27071);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (166, '空中之城', 1, '2021-04-23 08:00:00', 120, '《空中之城》讲述了面临中年危机酒吧调酒师、富有的商人太太、丧偶的老军人三个迥然不同的人物，面对各自的困难和尊严的故事。', 6.7, 1080000, '中国大陆', '剧情／爱情', 'https://p0.meituan.net/movie/622643b00ccdcb35eb5b587e121210ec276192.jpg@218w_300h_1e_1c', 66778, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (167, '情书', 1, '2021-05-20 08:00:00', 120, '影片由一封书信揭开了一段尘封已久的爱情往事。日本神户某个飘雪的冬日，渡边博子（中山美穗 饰）在前未婚夫藤井树（柏原崇 饰）的三周年祭日上因为无法抑制住对已逝恋人的思念，在其中学同学录里发现“藤井树” 在小樽市读书时的地址，依循着寄发了一封本以为是发往天国的情书。不久后她竟然收到署名为“藤井树”（中山美穗 饰）的回信，两人在信件的互访中揭开了一段发生在少年时期，两位名字相同的男女之间，一段懵懂青涩的爱情记忆。', 8.9, 59990000, '日本', '剧情／爱情', 'https://p0.meituan.net/moviemachine/8503c98fa2a02df648f6a59448c4c99078837.jpg@218w_300h_1e_1c', 524000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (168, '超能一家人', 2, '2022-02-01 08:00:00', 120, '郑前（艾伦 饰）出生在一个叫喀西契克的远东城市，因与家人关系疏远，离家出走。如今重回故里，只为参加爷爷葬礼。没想到，天降陨石竟让爷爷死而复生，而一家人只要在一起竟能触发超能力，可有一人离开，超能力便立刻消失。与此同时，喀西契克邪恶市长乞乞科夫（沈腾 饰）正想方设法抢夺郑前开发的“理财神器”APP。郑前被迫和超能力家人团结起来，共同抵抗乞乞科夫····', 0, 0, '中国大陆', '喜剧／奇幻／动作', 'https://p0.meituan.net/movie/5311ffb24cf676bd9b51764fe842cd9a4665762.jpg@218w_300h_1e_1c', 0, 56424);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (169, '盛夏未来', 2, '2021-08-14 08:00:00', 120, '即将高考的陈辰（张子枫 饰）因发现妈妈（郝蕾 饰）与王叔叔（祖峰 饰）的“外遇”而心情低落，却在这时与校园网红郑宇星（吴磊 饰）因为一个谎言意外相识。两人在未解的青春里，携手面对成长的种种难题，勇敢地面对真实的自己，亦在这个夏天过去后，见证了彼此的成长和蜕变……', 0, 0, '中国大陆', '剧情／青春', 'https://p0.meituan.net/movie/e68687f670a47436752d5adf86aa895b80663.jpg@218w_300h_1e_1c', 0, 78907);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (170, '铁道英雄', 2, '2021-10-01 08:00:00', 120, '影片讲述了抗日战争时期，临城枣庄英勇的“铁道队”与敌人斗智斗勇，殊死捍卫家园的故事。本片是根据发生在1935年-1945年间，洪振海、王志胜、杜季伟、刘金山等“铁道队”队员的真实事迹改编，将最大限度地还原“铁道队”的英雄故事。', 0, 0, '中国大陆', '历史／战争', 'https://p1.meituan.net/moviemachine/2cde6f1c83a944592698b94daea08add258448.jpg@218w_300h_1e_1c', 0, 14620);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (171, '一百零八', 1, '2021-05-15 08:00:00', 120, '驻唱歌手肖兰离开成都前往什市，试图与男友陆地和好，却惨遭闭门之痛，绝望之际决定引产。肖兰医院引产遭拒，沮丧之时突遇地震袭击。 迟院长（妇产医院院长）率部保护全院近20余名孕妇紧急转至安全区罗汉寺，寺院住持破例收留众人，自此寺院变产院，禅房变产房。 这期间肖兰目睹了地震对生命的摧残和母亲们的负重，亲历了灾难中人性极限的撕裂与挑战，同时也见证了108个新生命在极端时期辉煌的诞生。', 7.9, 28590000, '中国大陆', '剧情', 'https://p0.meituan.net/movie/39b255d52f73963a5521fae7e1fa10da1295357.png@218w_300h_1e_1c', 448000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (172, '扬名立万', 2, '2021-11-19 08:00:00', 120, '一群不得志的电影人凑在一起，筹划拍摄一部关于上海轰动一时惊天大案的电影，借此翻身扬名，却不成想，案件背后的真相，远比他们虚构的电影情节更加离奇。', 0, 0, '中国大陆', '喜剧／悬疑', 'https://p0.meituan.net/moviemachine/20c35a6442a51cdf587974fda97df088135370.jpg@218w_300h_1e_1c', 0, 20700);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (173, '速度与激情9', 1, '2021-05-21 08:00:00', 120, '范·迪塞尔饰演的唐老大多姆·托莱多，与莱蒂和他的儿子小布莱恩，过上了远离纷扰的平静生活。然而他们也知道，安宁之下总潜藏着危机。这一次，为了保护他所爱的人，唐老大不得不直面过去。他和伙伴们面临的是一场足以引起世界动荡的阴谋，以及一个前所未遇的一流杀手和高能车手。而这个名叫雅各布（约翰·塞纳饰）的人，恰巧是多姆遗落在外的亲弟弟。', 7.5, 1191000000, '美国', '动作／犯罪', 'https://p1.meituan.net/moviemachine/fdd380fb2f7f0f1742f9f7c8ae7c5fec7189073.jpg@218w_300h_1e_1c', 15600, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (175, '独行月球', 2, '2022-01-01 08:00:00', 120, '2033年，为了抵御小行星的撞击，拯救地球，人类在月球部署了月盾计划。全员撤离时，维修工独孤月（沈腾 饰）被领队马蓝星（马丽 饰）落在了月球。不料月盾计划失败，独孤月成为了“宇宙最后的人类”，开始了他在月球上破罐子破摔的生活……', 0, 0, '中国大陆', '喜剧／科幻', 'https://p1.meituan.net/moviemachine/39d5f0da76bf0a7e422dc17de99e4980128349.jpg@218w_300h_1e_1c', 0, 66417);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (176, '雄狮少年', 2, '2021-08-06 08:00:00', 120, '本片讲述了留守少年阿娟和好友阿猫、阿狗在退役狮王咸鱼强的培训下参加舞狮比赛，经过重重磨砺，从病猫变成雄狮的成长故事。', 0, 0, '中国大陆', '动画', 'https://p0.meituan.net/movie/47b74994fca3d3e1b7eb2a56ec73d3ed2755146.jpg@218w_300h_1e_1c', 0, 19132);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (177, '超越', 2, '2021-06-12 08:00:00', 120, '百米冠军吴添翼为解开无法战胜老飞人郝超越的心结而返乡，不料郝超越早已沉沦，逃避最爱的跑步事业，更处心积虑利用吴添翼谋利。师兄弟二人的较量从回忆中的跑道延伸进现实生活，历经误会和冲突，最终冰释前嫌并互相治愈······', 0, 0, '中国大陆', '运动／剧情／喜剧', 'https://p0.meituan.net/movie/9c91fd2f898f667049c7f94aeceaf333936391.jpg@218w_300h_1e_1c', 0, 172931);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (178, '狗果定理', 2, '2021-06-12 08:00:00', 120, '一个卷入夕阳三角恋的俏奶奶，一个陷入连环三角债的公司老板，一对挣扎在现实生活与浪漫情感中间的都市情侣，一个游离在城市边缘酸甜苦辣的“三口”之家……种种机缘巧合之下，因为一只神奇狗狗“果汁儿”的出现，所有人的命运都开始发生变化 ……', 0, 0, '中国大陆', '剧情／喜剧', 'https://p1.meituan.net/movie/1507b9900d5c4eda7c34a259ca99dc103958590.jpg@218w_300h_1e_1c', 0, 26042);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (179, '封神三部曲', 2, '2021-07-12 08:00:00', 120, '《封神三部曲》取材于中国家喻户晓的古典神魔小说名著《封神演义》，讲述一场三千多年前人、仙、妖之间旷日持久的神话战争，谱写一部波澜壮阔的中国神话史诗。', 0, 0, '中国大陆', '战争／奇幻／古装', 'https://p0.meituan.net/movie/560dd261f34dff9c05f9e7d584ce7333986296.png@218w_300h_1e_1c', 0, 58959);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (180, '你好世界', 2, '2021-06-11 08:00:00', 120, '在京都居住的内向男高中生直实（北村匠海 配音）的面前，突然出现从10年后穿越而来26岁的自己（松坂桃李 配音）。未来的直实告诉他，自己不久便会与琉璃（滨边美波 配音）相爱，可是之后烟花大会时她却会因为一场事故意外离世。为了拯救爱人，16岁的直实卷入了这场现实与虚拟的记忆世界，经历了一系列超乎想象的事情。即使世界毁灭，我也想再见你一面。', 0, 0, '日本', '爱情／奇幻／动画', 'https://p0.meituan.net/movie/65c751d508f5256aa09fe797144f7e6c3279372.jpg@218w_300h_1e_1c', 0, 151271);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (182, '遇见喵星人', 2, '2021-06-18 08:00:00', 120, '大四学生李玄武是个对数字不敏感的学生。这天，他在补考高数的考场做了一个梦， 梦里的世界大不同，穷同学秦隆变成了富二代，舅舅变成了一个骗吃骗喝的假道士，唯一不变的就是他的数学成绩依然很差，补考又没及格。天无绝人之路，空中飘来一张高数补习班的宣传单，上面还有美女讲师“小甜甜”的照片，李玄武决定去碰碰运气，偶遇美女老师朱怯怯。为了摆脱数字怪的控制，拯救小伙伴，李玄武痛定思痛，终于领悟到只有通过好好学习才能提升高数能力……', 0, 0, '中国大陆', '喜剧／科幻', 'https://p0.meituan.net/movie/cddc2aeb00eee95af7758f3a6d1a8cff4144804.jpg@218w_300h_1e_1c', 0, 5659);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (183, '阳光姐妹淘', 2, '2021-06-11 08:00:00', 120, '当年陪你走过青春的好朋友，后来还有联系吗？大家又各自过得好吗？家庭主妇张丽君在医院重逢了病重的高中好姐妹林青，林青希望离世前能跟当年的姐妹们重聚。于是，张丽君开启了寻找姐妹的旅程，这一路，大家找回了青春，也重新审视了当下生活。时间好快，人已长大，好想和你再相聚。', 0, 210000, '中国大陆', '喜剧／剧情／青春', 'https://p1.meituan.net/movie/b161d983e9385e178fb84420a0baa22e3536469.jpg@218w_300h_1e_1c', null, 141804);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (184, '外太空的莫扎特', 2, '2021-06-11 08:00:00', 120, '任大望（黄渤 饰）一心想把喜好天文的儿子任小天（荣梓杉 饰）培养成钢琴演奏家，为此父子争吵不断。一天，神秘外星人莫扎特意外出现，从此，莫扎特帮助任小天开启了和爸爸“斗智斗勇”的生活。令任小天没有想到的是，莫扎特来到地球竟另有任务……', 0, 0, '中国大陆', '科幻／喜剧', 'https://p0.meituan.net/moviemachine/a6651103d1fca953661bd92721a5ae481329672.jpg@218w_300h_1e_1c', 0, 39011);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (185, '深爱', 2, '2021-08-13 08:00:00', 120, '“在这座城市里拥有爱情，我配么？”这也许是每一个“漂一族”的自我怀疑。一次交通意外，让王惜月（王智饰）陷入了一场从未预想的“一见钟情”；同为职场精英的何娜（克拉拉饰），一贯洒脱对待爱情的心态也面临着动摇。这个原本对她们来说只是生存战场的城市，突然变得更为复杂了……谎言之上的爱情岌岌可危，朋友名义下的快乐转瞬即逝，两段需要抉择的“深爱”，她们的答案，是否会和你一样？', 0, 123000, '中国大陆', '剧情／喜剧／爱情', 'https://p1.meituan.net/moviemachine/e0b739ee4a8f14f8b524e738cf02b6f8172629.jpg@218w_300h_1e_1c', 23499, 46061);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (186, '冰雪大作战2', 1, '2021-04-30 08:00:00', 120, '小镇上一年一度的雪橇比赛就快到了。四眼小队要对阵小镇新天才扎克和他的堂姐查理。扎克是一个科学天才，满屋的仪器设备和科学奖状奖杯。在赛场，大家逐个展示了他们的雪橇，跃跃欲试。在激烈的比赛后，扎克赢得了胜利。弗兰克（四眼）发现扎克作弊了，大家决定重新比赛。他们要修建一个更加惊险、曲折的赛道！四眼孤注一掷，竟然用谷仓做赌注，但是比赛前他又后悔了，想在没有人因为失去谷仓而难过之前退出。皮尔斯（第一部中小狗的主人）看到了沮丧的弗兰克并安慰他，让他振作起来，因为弗兰克一直是一个从不放弃从不言败的人。在激烈的雪橇比赛后，弗兰克最终赢得了胜利，谷仓终于保住了。而且所有人都原谅了扎克，每个人在雪地中拥抱了他。从此，他们像一家人那样聚在谷仓里。', 7.7, 12020000, '中国大陆', '喜剧／动画／冒险／家庭', 'https://p0.meituan.net/movie/d1cbb14acdc8d3b3226b80b74f73509d4272699.jpg@218w_300h_1e_1c', 12344, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (187, '你的婚礼', 1, '2021-04-30 08:00:00', 120, '那个陪你穿校服的人，最后陪你走到了婚礼吗？电影《你的婚礼》讲述游泳特长生周潇齐与转校生尤咏慈长达15年的爱情故事。高中时，周潇齐（许光汉饰）对尤咏慈（章若楠饰）一见钟情，年少懵懂的纯纯爱恋，男孩默默守护，但女孩却不告而别。此后的人生，15年的爱情长跑。你的婚礼，也是我的成人礼。', 7.9, 789000000, '中国大陆', '爱情／剧情', 'https://p1.meituan.net/movie/5778f9b9f2909ba55eb99bec059cb5d22333886.jpg@218w_300h_1e_1c', 21313131, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (188, '你好，李焕英', 1, '2021-02-12 08:00:00', 120, '2001年的某一天，刚刚考上大学的贾晓玲（贾玲 饰）经历了人生中的一次大起大落。一心想要成为母亲骄傲的她却因母亲突遭严重意外，而悲痛万分。在贾晓玲情绪崩溃的状态下，竟意外的回到了 1981 年，并与年轻的母亲李焕英（张小斐 饰）相遇，二人形影不离，宛如闺蜜。与此同时，也结识了一群天真善良的好朋友。晓玲以为来到了这片“广阔天地”，她可以凭借自己超前的思维，让母亲“大有作为”，但结果却让晓玲感到意外......', 9.5, 5413000000, '中国大陆', '喜剧／剧情', 'https://p0.meituan.net/movie/48774506dc0e68805bc25d2cd087d1024316392.jpg@218w_300h_1e_1c', 324234, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (189, '唐人街探案3', 1, '2021-02-12 08:00:00', 120, '继曼谷、纽约之后，东京再出大案。唐人街神探唐仁（王宝强 饰）、秦风（刘昊然 饰）受侦探野田昊（妻夫木聪 饰）的邀请前往破案。“CRIMASTER世界侦探排行榜”中的侦探们闻讯后也齐聚东京，加入挑战，而排名第一Q的现身，让这个大案更加扑朔迷离，一场亚洲最强神探之间的较量即将爆笑展开……', 8.8, 4523000000, '中国大陆', '喜剧／悬疑', 'https://p1.meituan.net/movie/ece9ff81e6f0af2c859aa151e42a33312706648.jpg@218w_300h_1e_1c', 42342, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (190, '悬崖之上', 1, '2021-04-30 08:00:00', 120, '上世纪三十年代，四位曾在苏联接受特训的共产党特工组成任务小队，回国执行代号为“乌特拉”的秘密行动。由于叛徒的出卖，他们从跳伞降落的第一刻起， 就已置身于敌人布下的罗网之中。同志能否脱身，任务能否完成，雪一直下，立于“悬崖之上”的行动小组面临严峻考验。', 9.1, 1103000000, '中国大陆', '剧情／悬疑／动作', 'https://p0.meituan.net/movie/3ad18f011110130e927d50046fde86f71038961.jpg@218w_300h_1e_1c', 4492000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (191, '秘密访客', 1, '2021-05-01 08:00:00', 120, '校车司机于困樵（段奕宏 饰）因失职酿成了一起重大的校车车祸，困樵拼死救出了似与车祸背后真相息息相关的汪楚祺（荣梓杉 饰），校车上的其他学生无人生还。得知此事家境显赫的汪父（郭富城饰）为了隐藏这个秘密，不得不把困樵藏在自己家的地下室里长达三年，却处处暗示困樵何时自首。随着访客的到来，这个原本温馨和睦的四口之家变得越来越诡异，母亲（许玮甯饰）整日惴惴不安，姐姐（张子枫饰）举止一反常态，家人个个心怀鬼胎，怪事频出，这个家背后不可告人的秘密似乎即将浮出水面……', 7.7, 215000000, '中国大陆', '悬疑／犯罪／剧情', 'https://p1.meituan.net/movie/a81eee7d4103deaf238cf93fcffd215c171907.jpg@218w_300h_1e_1c', 2434242, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (192, '我的姐姐', 1, '2021-04-02 08:00:00', 120, '一场意外车祸把父母带走，也把素未谋面的亲弟弟带到姐姐的面前。在一系列风波过后，姐姐原本来自原生家庭的伤痛慢慢被治愈，她也成长为更好的自己。', 8.9, 860000000, '中国大陆', '剧情／家庭', 'https://p0.meituan.net/movie/e2ecb7beb8dadc9f07f2fad9820459f92275588.jpg@218w_300h_1e_1c', 234242, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (193, '扫黑·决战', 1, '2021-05-01 08:00:00', 120, '该片是国内首部以“扫黑除恶”行动为题材、展现全国扫黑办督办案件的影视化作品，讲述某扫黑专案组组长宋一锐带领组员由一起征地案深入调查，突破层层阻挠，与幕后的黑恶势力展开激烈斗争，最终揭开腐败官员利用公职权力充当犯罪保护伞的虚伪面具，将犯罪分子绳之以法的故事。', 8.8, 400000000, '中国大陆', '犯罪／剧情／动作', 'https://p0.meituan.net/moviemachine/3084e88f63eef2c6a0df576153a3fad0327782.jpg@218w_300h_1e_1c', 772000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (194, '人之怒', 1, '2021-05-10 08:00:00', 120, '在一次全副武装的运钞车劫案中，梅斯先生（杰森·斯坦森饰）的儿子作为案发目击者被劫匪杀害。怒火中烧的梅斯为报丧子之仇，化身H卧底成为运钞车安保，试图找出杀害他儿子的劫匪以及与劫匪暗中勾结的安保公司内鬼。', 8.8, 157000000, '中国大陆', '动作', 'https://p0.meituan.net/moviemachine/e1178cc2abab5938c2113876da248fe0286888.jpg@218w_300h_1e_1c', 567000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (195, '八月未央', 1, '2021-04-16 08:00:00', 120, '讲述了可爱率真的小乔（谭松韵 饰）遇见了沉默寡言的未央（钟楚曦 饰），小乔的热情开朗使性格迥异的她们迅速成为了“蜜友”，而小乔未婚夫朝颜（罗晋 饰）的出现使得三人的感情产生了微妙的变化，三人关系也由此变得错综复杂的爱情故事。', 6.8, 67950000, '中国大陆', '爱情／剧情／青春', 'https://p0.meituan.net/movie/056d4f1a658e3e35c9f9e6c44c2aa3fe1831930.jpg@218w_300h_1e_1c', 243242, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (196, '名侦探柯南：绯色的子弹', 1, '2021-04-17 08:00:00', 120, '全球最⼤体育盛事WSG即将在东京举办开幕式，届时，融合⽇本先进技术打造的最⾼时速可达1000km的 “真空超电导磁悬浮”也即将通车。在这个万众瞩⽬的关头，汇集WSG知名赞助商的酒会现场遇到了突发事件，知名企业的⾼官接连遭到绑架，列车启动仪式现场也出现了意外。根据柯南的推理，本次事件也许与⼗五年前在美国波⼠顿发⽣的WSG绑架事件有关。这真的是偶然吗？在各国游客聚集的⽇本到底会发⽣什么样的事件？真空超导磁悬浮启动之时，“危险⼀ 家⼈”终于集结！', 8.3, 214000000, '中国大陆', '动作／动画／悬疑', 'https://p1.meituan.net/moviemachine/1d67ba8aec840c3e7ecdac6cdbd915c7293706.jpg@218w_300h_1e_1c', 23442432, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (197, '指环王：双塔奇兵', 1, '2021-04-23 08:00:00', 120, '改编自J • R • R •托尔金的史诗巨著，在《指环王：双塔奇兵》中，弗罗多·巴金斯和其他“护戒使者”联盟的成员为了熔毁至尊戒而兵分两路，继续着艰难险阻的中土世界护戒之旅。两支小分队的命运都与双塔紧密联系——一塔是被腐败堕落的巫师萨茹曼占据的位于艾森加德的欧尔桑克塔，另一塔则是黑暗魔君索隆的堡垒，位于巴拉督尔要塞的黑暗之地魔多。弗罗多与朋友们要承担巨大的风险，才有可能扭转乾坤。', 9.2, 74180000, '中国大陆', '剧情／动作／奇幻／冒险', 'https://p1.meituan.net/moviemachine/12437324cea0f64762e61d3341f2743b243014.jpg@218w_300h_1e_1c', 738200, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (198, '追虎擒龙', 1, '2021-05-01 08:00:00', 120, '故事讲述 1973 年香港警黑勾结， 在英国人指使下贪污巨利，荼毒市民。港督成立廉政公署，最先两位检察官为黑白双煞 （古天乐、林家栋）历尽千辛万苦调查权倾黑白两道的总华探长徐乐（吴镇宇）及跛豪（梁家辉），终把二人绳之以法的故事。', 8.7, 243000000, '中国大陆', '犯罪／剧情／动作', 'https://p0.meituan.net/movie/67a1fe8a23b913b305a37fed1c54b7a35019988.jpg@218w_300h_1e_1c', 4324546, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (199, '指环王：护戒使者', 1, '2021-04-16 08:00:00', 120, '比尔博·巴金斯（伊安·霍姆 饰）是100多岁的霍比特人，住在故乡夏尔，生性喜欢冒险，在年轻时的一次探险经历中，他从怪物咕噜手中得到了至尊魔戒，这枚戒指是黑暗魔君索伦打造的至尊魔戒，拥有奴役世界的邪恶力量，能够统领其他几枚力量之戒，在3000年前的人类联盟和半兽人大军的战役中，联盟取得了胜利，并得到了至尊魔戒，数千年的辗转后，魔戒落到咕噜手中，被比尔博碰巧得到。 因为和魔戒的朝夕相处，比尔博的心性也受到了影响，在他111岁的生日宴会上，他决定把一切都留给侄子弗罗多（伊利亚·伍德 饰），继续冒险。 比尔博的好朋友灰袍巫师甘道夫（伊恩·麦克莱恩 饰）知道至尊魔戒的秘密，同时，黑暗魔君索伦已经知道他的魔戒落在霍比特人的手中。索伦正在重新集结无数的半兽人，准备以大军夺取魔戒，征服世界。 甘道夫说服弗罗多将魔戒护送到精灵王国瑞文戴尔，弗罗多在好朋友山姆、皮平和梅利的陪同下，在跃马旅店得到了刚铎王子阿拉贡（维果·莫特森 饰）的帮助，历经艰难，终于到达了精灵王国。 然而，精灵族并不愿意保管这个邪恶的至尊魔戒，中土各国代表开会讨论，达成意见，准备将至尊魔戒送到末日山脉的烈焰中彻底销毁，弗罗多挺身而出接受了这个任务，这次，陪伴他的除了三个好朋友，还有甘道夫、阿拉贡、精灵莱格拉斯（奥兰多·布鲁姆 饰）、人类博罗米尔、矮人金雳。 一路上，护戒使者们除了要逃避戒灵和半兽人的追杀之外，更要抵抗至尊魔戒本身的邪恶诱惑，前途困难重重。', 9, 117000000, '中国大陆', '剧情／动作／奇幻／冒险', 'https://p0.meituan.net/moviemachine/c7faecae8e516492c3310b0c92e617061386707.png@218w_300h_1e_1c', 4566564, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (200, '真·三国无双', 1, '2021-05-01 08:00:00', 120, '由光荣特库摩正式授权，改编自畅销20年同名游戏的《真·三国无双》讲述动荡的东汉末年，汉室摇摇欲坠，董卓入京霸占朝野，引起天下动荡，身怀绝世武艺的曹操、吕布、关羽、刘备、张飞等各路英雄豪杰纷纷崛起，一场群雄逐鹿的大混战正式拉开序幕。在乱世中，到底谁才是真正的无双英雄？', 7.4, 15570000, '中国大陆', '动作／奇幻／战争', 'https://p1.meituan.net/movie/59e22ca6fcbb91783a6c7e7d872dfd601772824.jpg@218w_300h_1e_1c', 123000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (201, '阳光劫匪', 1, '2021-05-01 08:00:00', 120, '阳光（马丽 饰）经营着一家寻宠事务所，接受委托帮忙寻找丢失的宠物。有天，晓雪（宋佳 饰）找到了阳光，请她找回自己相依为命的女儿娜娜。阳光被晓雪感动，决定出手相助，但没想到“娜娜”竟然是一只五岁的大老虎，一行人开始了一场令人捧腹又惊心动魄的欢乐劫虎之旅。', 7.2, 43730000, '中国大陆', '喜剧／奇幻／剧情', 'https://p0.meituan.net/movie/d4d0a343206a7f143c5d1462a2b00e722829176.jpg@218w_300h_1e_1c', 78999, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (202, '哥斯拉大战金刚', 1, '2021-03-26 08:00:00', 120, '影片中，这两位宛如神衹一般强大的对手于一场壮观的战争中相遇，彼时世界命运正悬于一线。为了找到真正的家园，金刚与他的保护者们踏上了一次艰难的旅程。与他们一道前行的还有一个年轻的孤儿女孩——吉雅，这个女孩与金刚之间存在着一种独特而强大的紧密联系。但意想不到的是，他们在前行的航道上与愤怒的哥斯拉狭路相逢，也由此在全球引起了一系列破坏。一股无形的力量造成了这两只巨兽之间的巨大冲突，深藏在地心深处的奥秘也由此揭开。', 8.9, 1231000000, '中国大陆', '动作／冒险／科幻', 'https://p0.meituan.net/moviemachine/58732574fbc2f64c04c3e825c058da7f16742753.jpg@218w_300h_1e_1c', 7657567, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (203, '寻汉计', 1, '2021-05-01 08:00:00', 120, '2016年伊始，大龄女青年王招（任素汐 饰）离婚后发现自己怀了前夫的孩子，然而此时非婚生子女尚不能登记户口，千方百计想留下这个孩子的她，在姥爷（李保田 饰）的热心“帮助”下，开启了一段阴差阳错的“寻汉”之旅……', 8.2, 4040000, '中国大陆', '喜剧／爱情', 'https://p0.meituan.net/movie/8c943160455888982c294a461e4068653876247.png@218w_300h_1e_1c', 12344, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (204, '记忆切割', 1, '2021-04-23 08:00:00', 120, '花季少女张辛儿（郭采洁 饰），十岁那年意外看到了父母被枪杀的凶案现场，内心受到极大创伤。这永远无法抹去的痛苦记忆伴随她的成长。受张辛儿监护人辅导员刘梅（刘雪华 饰） 所托，著名生物科学家董教授（果靖霖 饰）通过切割记忆的方法，帮她重塑记忆，切割痛苦。原本顺利的记忆切割实验，却接连不断的发生意外、失控，当真相不断浮出水面，张辛儿最终将如何面对这一切？', 8.2, 3910000, '中国大陆', '悬疑／剧情', 'https://p0.meituan.net/movie/4140b0cedc1f93c1ccd3312cc6c4d911133775.jpg@218w_300h_1e_1c', 23355, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (205, '猪猪侠大电影·恐龙日记', 1, '2021-05-01 08:00:00', 120, '以阿五寻找猪猪侠为开端，讲述了见习超星特工阿五前往恐龙世界，结识了性格各异的伙伴们，在旅途中大家遭遇恶劣环境、恐龙袭击等种种危机，最后能够勇敢直面自己的弱点，相信伙伴的力量，从中得到了成长。', 8.4, 78110000, '中国大陆', '儿童／动画', 'https://p1.meituan.net/movie/c023971379638c11bc0baef0cf447b2b2034279.jpg@218w_300h_1e_1c', 3453453, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (206, '一百零八', 1, '2021-05-15 08:00:00', 120, '驻唱歌手肖兰离开成都前往什市，试图与男友陆地和好，却惨遭闭门之痛，绝望之际决定引产。肖兰医院引产遭拒，沮丧之时突遇地震袭击。 迟院长（妇产医院院长）率部保护全院近20余名孕妇紧急转至安全区罗汉寺，寺院住持破例收留众人，自此寺院变产院，禅房变产房。 这期间肖兰目睹了地震对生命的摧残和母亲们的负重，亲历了灾难中人性极限的撕裂与挑战，同时也见证了108个新生命在极端时期辉煌的诞生。', 8.4, 28590000, '中国大陆', '剧情', 'https://p0.meituan.net/movie/39b255d52f73963a5521fae7e1fa10da1295357.png@218w_300h_1e_1c', 435353, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (207, '西游记之再世妖王', 1, '2021-04-02 08:00:00', 120, '混沌初开之时，世上的第一只妖诞生，名元蒂，被奉为妖祖。千百万年后，昔日妖王孙悟空被唐僧从五行山下救出，一同前往西天取经。而传说中的妖祖元蒂这时再次出现，三界岌岌可危。两代妖王注定终极一战，但这次孙悟空遇上的，是前所未见的劲敌!', 8.3, 112000000, '中国大陆', '动画／奇幻／动作', 'https://p0.meituan.net/movie/d6e3e1dc75fd4fbcd3ebc7fb7c09f1d72989216.jpg@218w_300h_1e_1c', 354353, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (208, '金刚川', 3, '2020-10-23 08:00:00', 120, '1953年，抗美援朝战争进入最终阶段，志愿军在金城发动最后一场大型战役。为在指定时间到达，向 金城前线投放更多战力，志愿军战士们在物资匮乏、武装悬殊的情况下，不断抵御敌机狂轰滥炸，以血肉之躯一次次修补战火中的木桥。一段鲜为人知的历史，在暗流涌动的金刚川上徐徐展开......', 9.4, 1126000000, '中国大陆', '战争／剧情／历史', 'https://p1.meituan.net/moviemachine/5cbf9a626b7ed27c96ca3c748655b3ec2550103.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (209, '攀登者', 3, '2019-09-30 08:00:00', 120, '1960年5月，中国登山队向珠穆朗玛峰冲顶。危急时刻，临时队长方五洲（吴京 饰）为了保住同伴曲松林（张译 饰）的性命，不得已舍弃了摄影机。尽管他们从北坡成功登顶，却因缺少环绕山顶360度影像资料而不被国际登山界承认。不久，国家登山队解散，登顶的三人也各自天涯。1973年，国家再次组建登山队。经过艰苦的训练，方五洲、曲松林在气象学家徐缨的帮助下，带领李国梁（井柏然 饰）、杨光（胡歌 饰）等年轻登山队员，再次向珠峰发起挑战。在严峻多变的自然环境下，他们凭借顽强的毅力和信念，终于在1975年5月27日将五星红旗插在珠峰之巅。', 9.4, 1104000000, '中国大陆', '剧情／冒险', 'https://p0.meituan.net/moviemachine/735871b26fc55f7479087eae5e9f85722347572.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (211, '熊出没·狂野大陆', 1, '2021-02-12 08:00:00', 120, '什么才是快乐的真谛？一场起源于爱的冒险！狗熊岭附近新开业了一个神奇的“狂野大陆”，在那里，通过基因技术人可以自由变身各种动物，享受无比的自由和快乐。导游事业受挫的光头强，阴差阳错与神秘人乐天和熊二组队，参加乐园里奖金丰厚的比赛，却遭遇了队友乐天的背叛，历尽艰险终于获得冠军之际， 却意外发现狂野大陆中出现了肆意袭击他人的混合变身怪兽，同时也明白了队友乐天窃取基因手环的背后的真相。为了守护基因变身技术所承载的美好初衷，光头强与乐天冒险闯入总部，却再次发现在老板汤姆背后隐藏着真正的幕后黑手，他们是否能够获取最终的胜利？神秘人乐天背后又有着怎样的故事…', 8.8, 595000000, '中国大陆', '喜剧／科幻／动画', 'https://p0.meituan.net/moviemachine/72291e1cbc83311656e01e828ca79ddd2106074.jpg@218w_300h_1e_1c', 35443535, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (212, '寻龙传说', 1, '2021-03-05 08:00:00', 120, '很久以前，在充满奇幻色彩的龙佑之邦（Kumandra),人类和龙和谐地生活在一起。但当一股邪恶的力量袭来，龙牺牲了自己来拯救人类。500年后，同样的邪恶力量卷土重来，只有一个孤独的战士拉雅（Raya）承担重任，追踪传说中的最后一条龙，以修复这块破碎的土地，团结分裂的人民。在她的旅程中，她也会逐渐了解到拯救世界不仅仅需要一条龙，还需要信任和团队合作。', 9, 127000000, '中国大陆', '动画', 'https://p1.meituan.net/moviemachine/9be184648a639e0976887c9fd63385b36499634.jpg@218w_300h_1e_1c', 345353, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (213, '最可爱的人', 1, '2021-05-01 08:00:00', 120, '1950 年，朝鲜战争爆发，战火烧到鸭绿江两岸，严重威胁到了新中国的安全。无数优秀青年，为保卫来之不易的幸福家园、保卫新中国，参加了中国人民志愿军。杨根思、邱少云、黄继光、王海、张桃芳...... 他们是普通的志愿军战士，在那场保家卫国的战争中，他们无畏强敌、英勇战斗，谱写了壮丽的生命之歌，他们是最可爱的人！', 9, 3650000, '中国大陆', '战争／动画', 'https://p0.meituan.net/moviemachine/1049da5409a68309ecd64dc6bafd57f48879724.jpg@218w_300h_1e_1c', 234489, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (214, '波斯语课', 1, '2021-03-19 08:00:00', 120, '⼆战期间，犹太⼈吉尔斯在集中营为了⽣存，谎称⾃⼰是波斯⼈，被⼀位德国军官要求教授波斯语。对波 斯语完全不懂的吉尔斯只能凭空编造出⼀门语⾔，他开始⽤集中营中关押的犹太⼈的名字作为词根，编造 出上千个“波斯语”单词。吉尔斯凭借坚强的意志和聪明的头脑，最终活了下来。', 9.2, 36310000, '中国大陆', '剧情', 'https://p0.meituan.net/movie/b87e99c121df5dcb00ad6dddf61bd6ee2246726.jpg@218w_300h_1e_1c', 6456453, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (215, '杨善洲', 1, '2021-05-01 08:00:00', 120, '电影《杨善洲》深情讲述了原保⼭地委书记杨善洲的故事，塑造了⼀个永远把⼈民群众利益置于个⼈利益 之前的领导⼲部形象。在任上，他狠抓农业，专注民⽣；退休后，他绿化荒⼭，造福⼦孙。他⼀⼼为民，两袖清风，鞠躬尽瘁。青⼭作证，他六⼗年坚守共产党员的精神家园，他⽤⼀⽣践⾏着⼊党时的誓⾔：做⼀个永不退休的共产党员！', 9.2, 83030000, '中国大陆', '剧情／传记', 'https://p0.meituan.net/moviemachine/c4f816469681ce7c10e5c8642b124d472220970.jpg@218w_300h_1e_1c', 534535, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (216, '离开雷锋的日子', 1, '2021-05-01 08:00:00', 120, '乔安山和雷锋是最亲密的好战友。在一次执行任务中，乔安山在倒车时撞倒一根木杆，砸在指挥倒车的雷锋的太阳穴上，导致雷锋受伤牺牲。乔安山后悔不已，但各级领导并没有指责他，而是保护和鼓励他像雷锋学习。退伍后，乔安山一直以雷锋为榜样，全心全意为人民服务。退休前，他陪儿子一起开最后一班车，受到了雷锋志愿者的帮助。乔安山把雷锋志愿者的帽子给儿子戴上，要他和自己一样把雷锋精神一代一代地传下去。', 9.2, 12333445, '中国大陆', '剧情／传记', 'https://p0.meituan.net/moviemachine/0f0bff73e6e4f6bcdda102ecdec4b1c017415014.jpg@218w_300h_1e_1c', 4654657, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (218, '唐人街探案', 3, '2015-12-31 08:00:00', 120, '影片讲述了一对表叔侄被卷入离奇黄金凶杀案，多方追捕下到处逃窜途中发生的搞笑故事。天赋异禀的结巴少年秦风（刘昊然 饰）警校落榜，被姥姥遣送泰国找远房表舅“唐人街第一神探”唐仁（王宝强 饰）散心。不想一夜花天酒地后，唐仁沦为离奇凶案嫌疑人，不得不和秦风亡命天涯…… 穷追不舍的警探黄兰登（陈赫 饰）；无敌幸运的警探坤泰（肖央 饰）；穷凶极恶、阴差阳错的“匪帮三人组”；高深莫测的“唐人街教父”等悉数登场！他们能否在躲避警察追捕、匪帮追杀、黑帮围剿的同时，在短短七天内，完成找到失落的黄金、查明真凶、为自己洗清罪名这些逆天的任务？', 9, 823000000, '中国大陆', '喜剧／动作／悬疑', 'https://p0.meituan.net/moviemachine/905f799a205718d7ede9179b227bfcc99059007.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (219, '夏洛特烦恼', 3, '2015-09-30 08:00:00', 120, '在学生时代的初恋秋雅（王智饰）的婚礼上，毕业后吃软饭靠老婆养的夏洛（沈腾饰）假充大款，出尽其丑，中间还被老婆马冬梅（马丽饰）戳穿暴捶。混乱之中，夏洛意外穿越时空，回到了1997年的学生时代的课堂里。他懵懵懂懂，以为是场真实感极强的梦，于是痛揍王老师，强吻秋雅，还尝试跳楼让自己醒来。当受伤的他从病床上苏醒时，他意识到自己真的穿越了时空。既然有机会重新来过，那不如好好折腾一回。他勇敢追求秋雅、奚落优等生袁华（尹正饰）、拒绝马冬梅的死缠烂打。后来夏洛凭借“创作”朴树、窦唯等人的成名曲而进入娱乐圈。他的人生发生翻天覆地的巨变，但是内心某个地方却越来越感到空虚……', 9.3, 1445000000, '中国大陆', '喜剧／爱情', 'https://p1.meituan.net/movie/43d0e1bfbda2b50d7be2b70873a87e59386692.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (220, '金钱帝国', 3, '2009-04-30 08:00:00', 120, '六十年代，香港警察受贿成风，赌档粉档与中间人、警方组成的利益链大肆吸金，十年内警方受贿数额达当时的百亿港币之巨。九龙城区便衣火麒麟（黄秋生 饰）为破案不择手段，上峰施压，火麒麟如常去街上拉人顶罪，大学生韩志邦（方力申 饰）遭其陷害毒打，韩母托老警员找到警方与帮会的中间人猪油仔（王晶 饰）出面搭救得脱。人称乐哥的徐乐功（梁家辉 饰）时任华人总探，为人狂妄，出手大方。陈细九（陈奕迅 饰）心地善良尤擅察言观色，深得乐哥赏识，短短五年之内由普通便衣晋升至筲箕湾区探长，但代价之一是家中为掩人耳目收留了各位大哥的九名情妇……1974年，香港的贪污恶风震动国际，廉政公署成立，长官严国梁（林保怡 饰）聘用大学生韩志邦、失意警员火麒麟等组成第一批廉政专员队伍，严厉打击香港的地下规则。陈细九因贪污案发人财两空之际，却意外的发现了此生真爱……', 6, 19300000, '中国大陆', '犯罪', 'https://p0.meituan.net/movie/8de25a1d59f6121903d160625c0d3b2f400933.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (223, '战狼', 3, '2015-04-02 08:00:00', 120, '在南疆围捕贩毒分子的行动中，特种部队狙击手冷锋（吴京 饰）公然违抗上级的命令，开枪射杀伤害了战友的暴徒武吉（周晓鸥 饰）。这一行动令冷锋遭到军方禁闭甚至强制退伍的处罚，不过各特种部队精英组成超级特种部队战狼中队的中队长龙小云（余男 饰），却十分欣赏这个敢作敢为且业务过硬的血性男儿，于是将其召入自己的麾下。在新近的一次演习中，冷锋凭借冷静的判断力从老首长处拔得一城，并且成功击退了突然出现的狼群。谁知在毫无准备的情况下，战狼遭到了一伙荷枪实弹分子的袭击。原来武吉的哥哥敏登（倪大红 饰）是一个冷酷无情的国际通缉犯，他手下豢养了一大批身怀绝技的雇佣兵。为了给弟弟报仇，敏登派出雇佣兵千里迢迢奔着冷锋而来……', 9.3, 545000000, '中国大陆', '动作／战争', 'https://p0.meituan.net/moviemachine/152f043cabbc6feb9153f89203872657977832.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (224, '建党伟业', 3, '2011-06-15 08:00:00', 120, '1911年10月10日，武昌起义掀开了中国历史新的篇章。在此之后，孙中山（马少骅 饰）回国，就任中华民国临时大总统，次年建立中华民国临时政府。然而封建王朝的没落，共和的表象下中华民族的磨难仍在继续。袁世凯（周润发 饰）篡得大总统之位，更与日本秘密签订二十一条，意在复辟帝制。在举国上下的声讨中，复辟闹剧黯然落幕，军阀混战又带来新的战火与伤痛。国难当头，爱国志士与热血青年无不痛彻心扉，义愤填膺。有的人一心效法西方先进的理念与制度，有的人尊古复礼、修缮己身。陈独秀（冯远征 饰）、李大钊（张嘉译 饰）、毛泽东（刘烨 饰）等则在十月革命的胜利中看到中华民族新的希望。这部影片讲述的，正是中国共产党诞生前夜的故事……', 7, 409000000, '中国大陆', '剧情／历史', 'https://p0.meituan.net/movie/1dfb663bb3f2715a39a907483de97757419624.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (225, '催眠大师', 3, '2014-04-29 08:00:00', 120, '国内擅长催眠疗法的知名心理治疗师徐瑞宁（ 徐峥饰）正值 事业风生水起之时，由他治疗过的病人全都痊愈出院，这也使得徐瑞宁信心倍增，直至骄傲自大。就连自己的学生在课堂上对他理论的质疑，都毫不收敛的回击过去，这些都被徐瑞宁的大学老师方教授看在眼里。在徐瑞宁一次讲课后，方教授受到其学生的请求，让徐瑞宁诊断一位特殊的病人任小妍（ 莫文蔚 饰），但方教授嘱咐徐瑞宁，称这位病人的十分怪异，她看过的心理医师没有一名能够治疗她的病情，任小妍讲话虚实难辨而且懂得如何回击治疗师。方教授口中所说的这名病人，让徐瑞宁对她产生了极大的兴趣，并且自大的认为没有治不好的病人。当任小妍来到徐瑞宁的诊疗室时，徐瑞宁发现这位病患确实如同方教授 所说很有古怪。任小妍声称自己能够看到已经死去的人，并且寻求她的帮助完成生前没有做过的事，徐瑞宁听后不以为然，但任小妍解释自己根本就没有心理疾病，她所看到的都是真实存在的。徐瑞宁在任小妍一顿\\"疯语\\"过后，决定采用催眠疗法进行治疗。而当任小妍成功进入催眠状态后，徐瑞宁想要找出任小妍的心理阴影所在，却不曾想自己被带入了一个可怕的陷阱。', 8.9, 274000000, '中国大陆', '剧情／惊悚／悬疑', 'https://p1.meituan.net/movie/abc5def22ad52058cd4664ea8f16ff641701721.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (226, '同桌的你', 3, '2014-04-25 08:00:00', 120, '1993年的夏天，文静秀气、戴着一副小孔眼镜的转校生周小栀（ 周冬雨饰），第一次见到了自己的同桌——坐在最后一排的捣蛋男生林一（ 林更新饰），他们一起经历了初中、高中和大学的青葱岁月。林一对周小栀爱的誓言：“总有一天，我会带你去一个只有我们俩的地方。”一晃十年 。在美国工作的林一接到国内的快件，自己初中的同桌周小栀即将结婚，于是赶回国内参加她的婚礼，在回国的飞机上，不禁回忆起两人之间的往事。1993年的夏天，文静秀气、戴着一副小孔眼镜的周小栀与林一成为同桌，两人逐渐成为朋友。在班上，周小栀经常会受到同学的欺负，都是林一出面帮助她，有好几次林一与班内的一名同学大打出手。对于林一的帮助，周小栀满怀感激，在内心里面逐渐的接受了他，转眼初中结束，周小栀和林一都升入高中。升高中之后，林一为了和周小栀一起，特地转入她的班级，在一次值日活动中，周小栀的腿摔伤了，林一义不容辞的负责接送她上下学。那一年，中国大使馆被炸，林一不顾学校的劝解，毅然上街参加示威游行。周小栀到游行现场去找林一，被学校误以为是擅自出去参加游行，两人一起受处分。林一向周小栀表示想要她做自己的女朋友，周小栀为了鼓励林一，和他约定，只要两人一起升入同一所大学，自己就会成为他的女友，林一倍受鼓舞。高考结束之后，林一升入大学，在大学里林一意外的见到了周小栀，他们都没有考入理想的大学，却又一次巧合的进了同一所大学并再一次成为同学。周小栀向他提起自己想去美国留学。林一向周小栀讲起两人之间关于高考的约定。周小栀决定兑现自己的诺言，不过她与林一约定，两人之间每天只有5分钟是恋人关系，尽管时间短暂，林一对于周小栀接受自己还是很高兴。林一的室友王尔德、龚兵以及李潇，同样希望在大学期间找一个女友。林一接到周小栀爸爸的电话，他告诉林一是周小栀故意放弃去斯坦福的机会，去他所在的大学，同时寄给了林一斯坦福大学的资料，要他转交给周小栀。林一劝说周小栀去斯坦福，周小栀告诉他，自己不想去美国，不想离开他。为了帮助周小栀实现到斯坦福留学的梦想，林一决定陪她一起去美国，于是开始发奋学习英语，他的努力让室友们感到吃惊。周小栀与林一的关系日渐紧密，周小栀把两人之间的恋人时间延长到21分钟。林一的同学Tom对周小栀很有好感，对于他主动追求周小栀的行为，林一感到很反感，两人终于爆发了冲突，林一为此受到了学校的处分、周小栀对林一不理智的行为进行了处罚，把两人相处的时间从21分钟缩减到13分钟。周小栀找到Tom告诉他自己已经有男朋友了，就是林一，至此Tom明白自己已经没有机会。周小栀和林一密切的关系，终于有一天她发现自己怀孕了，于是找到林一，将这件事情告诉了他。林一带周小栀去做人流，没有想到却引起了大出血。周小栀被送往医院，林一被怒气冲冲的小栀爸爸赶走，周小栀康复出院之后对林一没来看自己感到失望，觉得他缺乏责任心，于是提出分手，同时她终于下定决心要到美国去留学。周小栀的离开让林一感到失望，他对周小栀感情很深。这时候非典爆发了，林一成为疑似病例被隔离，周小栀对林一的境况很是牵挂。当听说隔离区里面有人患非典之后，半夜，周小栀和林一的室友通过梯子爬上了林一的宿舍，在周小栀的劝说下，林一逃离了出来，两人再一次和好。周小栀和林一商定两人一起去美国，周小栀把两人相处的恋人时间重新设定为14分钟。转眼到了毕业的时候，林一顺利的通过了面试，拿到了去美国的签证，周小栀并没有成功，林一对于自己的将来踌躇满志，他和周小栀约定，一定会在美国等她。林一到了美国之后，经过十年的打拼，没有获得理想的成功，工作收入很低，婚姻和家庭也不理想。周小栀并没有如约来到美国，林一孤身一人在美国坚持，当他收到周小栀结婚的请帖时，变得有一些竭斯底里。其实，当年周小栀把林一送走之后，由于一直没有通过考试和签证拒签，她就没有成行，林一多次联系她，周小栀表示自己不愿意去美国了，就这样两人分开。林一拿着周小栀婚礼的请帖赶到宴会现场，很多同学见面，大家一阵寒暄。周小栀和林一再次见面，看见一袭婚纱在身的周小栀，林一有一种说不出的伤感。这时候自己之前的同学过来，把他拉到包厢内，大家在一起重新聚首，都有聊不完的话题，周小栀也在座，与林一相对，相顾无言。宴会散场，林一和周小栀坐在了一起，谈及往事，周小栀告诉林一，自己多次申请去美国可是没有通过，最后只好放弃，不再去出国。而她不想把林一叫回来，也不确定他还会不会为自己回来，两人分开是最现实的事情，美好的感情最终被现实打败。周小栀的婚礼在教堂举行，林一以及别的好友们悉数出席，当两人手挽手从教堂的长廊上走过，曾经的一幕幕再一次浮现在周小栀的脑海中，可是大家都已经回不去了，人生还是要按照现实的轨道去运行。林一在现场，看着周小栀终于找到了自己的幸福，最后一丝的希望破灭，两人从同桌开始，经历的青葱岁月已经成为过去。有时候希望是美好的，可是在实现梦想的过程中，我们总是被现实打败，爱情也是如此。 其实，周小栀每次约定做林一男朋友的时间是有寓意的，5211314，即我爱你一生一世。', 8.5, 456000000, '中国大陆', '剧情／爱情', 'https://p0.meituan.net/moviemachine/c0b9909be6e1cde62043dc2b54ff6159267516.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (227, '西游记之大圣归来', 3, '2015-07-10 08:00:00', 120, '大闹天宫后四百年多年，齐天大圣成了一个传说，在山妖横行的长安城，孤儿江流儿（林子杰 配音）与行脚僧法明（吴文伦 配音）相依为命，小小少年常常神往大闹天宫的孙悟空（张磊 配音）。 有一天，山妖（吴迪 配音）来劫掠童男童女，江流儿救了一个小女孩，惹得山妖追杀，他一路逃跑，跑进了五行山，盲打误撞地解除了孙悟空的封印。悟空自由之后只想回花果山，却无奈腕上封印未解，又欠江流儿人情，勉强地护送他回长安城。 一路上八戒（刘九容 配音）和白龙马也因缘际化地现身，但或落魄或魔性大发，英雄不再。妖王（童自荣 配音）为抢女童，布下夜店迷局，却发现悟空法力尽失，轻而易举地抓走了女童。悟空不愿再去救女童，江流儿决定自己去救。 日全食之日，在悬空寺，妖王准备将童男童女投入丹炉中，江流儿却冲进了道场，最后一战开始了……', 9.3, 955000000, '中国大陆', '动画／动作／奇幻', 'https://p0.meituan.net/movie/07b4127cff2289cd91b65628f4417ac37666558.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (228, '寻龙诀', 3, '2015-12-18 08:00:00', 120, '20世纪80年代末， 胡八一（ 陈坤饰）、 王凯旋（ 黄渤饰）和 Shirley杨（ 舒淇饰）在美国打算金盆洗手，本来叱咤风云的摸金校尉沦为街头小贩，被移民局追得满街跑。胡八一与未婚妻Shirley移居美国想从此过上普通人的生活，谁知在婚礼前夕却发现自己二十年前死在‘百眼窟’的初恋女友 丁思甜（ 杨颖 饰）当时念念不忘的彼岸花居然再次出现！胡八一、王凯旋、Shirley杨决定再入草原千年古墓 。', 9.1, 1680000000, '中国大陆', '动作／冒险／奇幻', 'https://p0.meituan.net/moviemachine/5075ae24afa597f97f5fbfefb9d2bdad478940.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (229, '倩女幽魂', 3, '2011-04-30 08:00:00', 120, '宁采臣 （张国荣饰）赴郭北县收帐，逢大雨，躲入传说纷纭的兰若寺投宿，但为寺内道士燕赤霞 （午马饰）所拒，于是宁采臣偷偷潜入寺中。入夜，宁采臣被一阵琴音吸引，邂逅少女聂小倩 （王祖贤饰）。聂小倩突露杀机，幸燕赤霞赶到，救下宁采臣一命。聂小倩感其正直，心生爱意，但被燕赤霞阻止。宁采臣却误认燕赤霞为杀人犯，欲与聂小倩逃走。树精姥姥 （刘兆铭饰）逼聂小倩杀宁采臣，聂小倩不肯，姥姥命众魔欲杀宁与小倩，燕赤霞再次出现，救 下二人，姥姥重伤。此时宁采臣才知聂小倩原是一游魂，因尸骨被弃荒野而受姥姥控制，每夜四处寻找壮男为姥姥吸引阳精，增益延寿。为免聂小倩再次受辱，宁采臣答应将其尸骨送回乡间安葬。不料聂小倩却被地府石妖抢去为妻。燕赤霞经不住宁采臣相求，再次出手，终于救回小倩 。', 9, 20000, '中国大陆', '爱情／奇幻／武侠／古装', 'https://p0.meituan.net/moviemachine/e5049c64cbfadfe75c7de6ad23247a8a268159.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (230, '后会无期', 3, '2014-07-24 08:00:00', 120, '故事开始于虚构的中国最东边的小岛“东极岛”，智力低下的青年胡生（高华阳 饰）讲述了关于他的两个哥们儿马浩汉（冯绍峰 饰）和江河（陈柏霖 饰）的故事。浩汉在外闯荡多年后回到故乡，却发现全岛面临着拆迁问题，而他最好的朋友，岛上唯一的老师江河也将被调到更为偏远的地区。于是 浩汉做出了一个决定，那就是开车送江河去三千多公里外的学校报到。兄弟三人踏上了前所未有的旅程，途中他们见了当临时演员的童年玩伴周沫（陈乔恩 饰），误入歧途的苏米（王珞丹 饰），还有浩汉心仪多年从未见面的笔友刘莺莺（袁泉 饰），以及不羁的摩托车手阿吕（钟汉良 饰）。看似平常的旅程却暗藏伏笔，浩汉和江河最终走到了终点，开始了截然不同的人生轨迹。', 8.3, 629000000, '中国大陆', '剧情', 'https://p1.meituan.net/moviemachine/9eaa0895ad341c778e0c2b135c734c6c536631.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (231, '智取威虎山', 3, '2014-12-23 08:00:00', 120, '全面内战时期，东北匪患横行。解放军某部剿匪小分队奔赴牡丹江，深入林海雪原，侦查员杨子荣与卫生员白茹赶来支援。为了瓦解土匪势力，杨子荣潜入土匪老窝威虎山。为了取得匪首座山雕的信任，杨子荣献出先遣图，被封为“老九”。然而狡猾多疑的座山雕还是给他设置了一系列考验，杨子荣一次次化险为夷。他在与土匪们周旋的同时，为山下的战友传递情报、组织战斗。在座山雕的寿辰“百鸡宴”上，小分队根据杨子荣的情报成功进攻威虎山并剿灭全部土匪。', 9.2, 883000000, '中国大陆', '剧情／动作／战争／冒险', 'https://p0.meituan.net/movie/6a742290a13925a8add5d7fc5f3cd285431185.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (232, '寒战', 3, '2012-11-08 08:00:00', 120, '作为亚洲金融中心的香港被称作“最安全的城市”，岂料警局接到一通匿名电话，对方声称劫持了警队的一辆前线冲锋车以及车内的五名警员和武器装备。此次事件引起了香港警队高层的高度重视，适逢处长（王敏德 饰）出访国外，此时由鹰派人物行动副处长李文彬（梁家辉 饰）与年轻的管理副处长刘杰辉（郭富城 饰）一起负责这次案件，并将行动命名为“寒战”，李文彬任总指挥。李文彬的儿子（彭于晏 饰）也是人质之一，刘杰辉因此对李文彬父子产生怀疑。与此同时，匪徒展开了进一步行动：警察被杀、爆炸案频发……到底是谁在将整个警队玩弄于鼓掌之中？香港真的是“最安全的城市”吗？', 8.3, 253000000, '中国大陆', '剧情／动作／犯罪', 'https://p1.meituan.net/moviemachine/9dad6cdbdee3747f624b455fa3950a77617969.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (233, '心花路放', 3, '2014-09-30 08:00:00', 120, '二手音响商耿浩（黄渤 饰）婚姻失败，他想用锤子爆小三（李晨 饰）的头，却迟迟没有勇气，幸亏在剧组做制片的兄弟郝义（徐峥 饰）及时发现自暴自弃的耿浩，他决定带着耿浩开启一段“治愈之旅”。于是一对好基友带着一只狗上路，邂逅三千公里的“桃花”。“阿凡达女郎”（陶慧 饰）、“杀马特”周丽娟（周冬雨 饰）、“白富美”（张俪 饰），各式各样的女人接连登场，耿浩一路艳遇一路疗伤。时光倒转，5年前的此时，大龄文艺女青年（袁泉 饰）因为听了一首流浪歌手耿浩的歌，毅然前往大理……在经历一连串奇葩遭遇之后，大家都放下了心里的“阴影”，找到了通向幸福的道路。', 9, 1170000000, '中国大陆', '喜剧／爱情', 'https://p0.meituan.net/moviemachine/d40217b11e2c750a9ac298fb65f5fc0e2960128.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (234, '前任攻略', 3, '2014-01-31 08:00:00', 120, '在一场婚礼上新郎（关喆饰）新娘（毛俊杰饰）设置了前男友桌和前女友桌，集合了新郎的前女友们和新娘的前男友们，埋伏了两场危机四伏的相遇。婚礼上，前男友桌和前女友桌或称兄道弟，或针锋相对，或真情流露，或举杯彷徨，男人和男人，女人和女人，男人和女人之间妙语连珠、嬉笑怒骂，折射出现代人当下的爱情百态。孟云（韩庚饰）是一个生活在北京，工作上小有成就的普通青年，在这场“别开生面”的前女友婚礼上，意外结识了出现在新郎设置的“前女友桌”中的女孩夏露（姚星彤饰）。就这样，孟云和前女友的老公的前女友开始了一段猝不及防的爱情。孟云与两个大学同学余飞（郑恺饰）和罗茜（王丽坤饰）合伙运营着一家小公司，三人从大学毕业初期的艰难创业岁月中起步走到现在，关于青春，他们珍藏着关于彼此的独家记忆。然而，罗茜对孟云的感情却从青春一路跨越伴随到当下，十四年，她默默守护着孟云，而孟云也在不自知的情况下爱着罗茜。在好哥们儿余飞和夏露闺蜜小文（班嘉佳饰）的陪伴与见证下，孟云与夏露的爱情经受着历任极品前任的轮番考验，神经质般脆弱的前女友玲玲（麦迪娜饰），喝酒上瘾的前女友萌萌（熊乃瑾饰），花钱如流水的前女友尚丹（柳岩饰），还有穷的只剩钱了的夏露前男友朴恩浩（李相烨饰）都或多或少的影响到了他们的爱情，当聪明女孩夏露一一将这些状况化险为夷时，却没想到罗茜会成为无法逾越的鸿沟。罗茜与一个多金又成熟细心的男人赵明（张涵予饰）订婚了，订婚宴上，喝多了的罗茜吐露真心，而孟云也发觉了被深埋藏在自己心底的对罗茜的爱情，夏露无法面对两人的感情，从订婚宴上逃走。随后夏露与孟云在吵架时失足掉下楼梯，导致夏露流产。孟云、夏露、罗茜将怎样收拾感情的残局，众人的感情的归宿最终何去何从？', 9, 130000000, '中国大陆', '喜剧／爱情', 'https://p0.meituan.net/moviemachine/4b792a11bf1b9f8ef20f615497b5117d2460540.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (235, '重返20岁', 3, '2015-01-08 08:00:00', 120, '七十岁的老太太沈梦君（归亚蕾 饰）年纪越大，脾气越大，渐渐的，除了管家李大海（王德顺 饰）还愿意留在她的身边，没有一个人愿意与她共处。在儿媳妇杨琴（李宜娟 饰）因压力过大而生病入院后，沈梦君终于被家人送入了养老院，气愤和悲伤之中，沈梦君决定在照相馆里留下自己最后的容颜，没想到却因此而开启了一段返老还童的奇幻旅程。一眨眼之间，满面皱纹的沈梦君变成了二十岁的妙龄少女孟丽君（杨子珊 饰），重获青春的沈梦君不仅体会到了年轻带来的机遇与活力，更品尝到了爱情的酸涩滋味。然而，就在她沉浸在幸福的幻觉中不能自拔之时，一场意外将她抛到了人生的十字路口之前，二十岁还是七十岁，沈梦君必须做出选择。', 9.2, 365000000, '中国大陆', '喜剧／爱情／奇幻', 'https://p0.meituan.net/moviemachine/ae8f1fe730a3121eadd40969faec59c3261310.webp@218w_300h_1e_1c', 12352000, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (236, '无间道', 3, '2003-09-05 08:00:00', 120, '1991年，18岁的三合会会员刘建明 （ 刘德华 饰）听从大哥韩琛 （ 曾志伟 饰）的指示进入警校学习，成为警方卧底。而同时警校中的另一名学生陈永仁 （ 梁朝伟 饰），受警方安排表面上将其强迫退学，实际上则是让他进入三合会当卧底。刘建明从警校毕业后顺利的进入警局，并且职位步步高升，已成为刑事情报科A队的一员，在此期间他利用各种机会为韩琛提供了大量的情报。而陈永仁在这些年以来已经得到了韩琛的初步信任，但由于韩琛的案件始终没有破，他永远只能呆在黑帮，只有黄警督 （ 黄秋生 饰）与他单线联系。2002年的一个晚上，根据陈永仁卧底情报，获知一批毒品即将交易，而交易的一方为韩琛，但由于当时刘建明及时将消息传给了韩琛使其成功逃脱，不过因此双方发现各自的内部俱有“内鬼”，于是一场激烈的角斗由此展开。在最后的结局中，双方“内鬼”都认出了自己的身份，不过刘建明抢先一步，已经将陈永仁在警察局的档案删除，但在删除之前，保留了一个备份，密码是女朋友Mary的生日。经过深思，刘建明决定做一个好人，请求陈永仁给他一次机会，陈永仁没有信，拷上了刘建明。最后陈永仁死在了韩琛的另一个警方卧底枪下，刘建明也杀死了那个开枪的卧底 。', 9.1, 20000, '中国大陆', '剧情／惊悚／犯罪', 'https://p0.meituan.net/movie/606de8f394d40dbcbb9b87943fec71a2130408.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (238, '十二生肖', 3, '2012-12-20 08:00:00', 120, '当年八国联军火烧圆明园，致使大批珍贵文物流落海外，其中四尊十二生肖兽首最引人关注，不仅惹出国内外的广泛争论，更有收藏家开出天价竞拍这几尊珍品。当然，其间不乏奸邪的文物贩子，试图通过偷盗的手段获取宝贝。以此为契机，正在度假的国际大盗JC（成龙 饰）隆重登场。JC背后有一支Simon（权相宇 饰）、David（廖凡 饰）、Bonnie（张蓝心 饰）等人共同组成的超专业团队，他们一同远赴巴黎，寻求国宝鉴定专家Coco（姚星彤 饰）的帮助。经过周密细致的准备，JC等人一步步逼近重兵把守的兽首，而围绕珍宝不可避免爆发连番惊险火爆的打斗与追逐。　　在这一过程中，JC似曾被利益和金钱泯灭的爱国之心渐渐苏醒……', 8.5, 882000000, '中国大陆', '动作／冒险／喜剧', 'https://p1.meituan.net/moviemachine/80480ff6eb5be5aa9f6f3bcfc68c313d1670997.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (239, '煎饼侠', 3, '2015-07-17 08:00:00', 120, '人气偶像大鹏（董成鹏 饰）遭遇了意外事件，令其跌入人生谷底，女友采洁（郭采洁 饰）也离他而去，只剩助理董成龙（潘斌龙 饰）和红颜知己柳岩（柳岩 饰）对他不离不弃。大鹏草率签下电影合约，却因资金问题一筹莫展，还遭到了黑道大哥王老板（梁超 饰）的威胁。无奈之下大鹏只得找来小 明星杜潇潇（袁姗姗 饰）、群头马涛（衣云鹤 饰）、狗仔摄影师胡来（崔志佳 饰）组成一个临时摄制组，准备拍摄一部全明星阵容的大片，而拍摄方式也是前所未有的尝试，邓超、吴君如、曾志伟、东北F4等一线大咖能否顺利出演？逼入绝境的大鹏将遭遇什么样的困难？', 8.5, 1160000000, '中国大陆', '喜剧', 'https://p1.meituan.net/movie/99a96ba63eaea1c09fa54920efc21c41233166.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (240, '万物生长', 3, '2015-04-17 08:00:00', 120, '秋水（韩庚 饰），医科大学的学生，与厚朴（张博宇 饰）、黄芪（杨迪 饰）、辛夷（赵一维 饰）是死党，正值青春期的他们在旺盛荷尔蒙支配下异常生猛，不停地寻找着“猎食”对象。 含苞待放的小满（李梦 饰）是秋水念念不忘的初恋；大腿紧实的白露（齐溪 饰）是与秋水互相奉献童贞的前任；熟透了的柳青（范冰冰 饰）是秋水眼中千娇百媚的女神；胸大无脑的娟儿（沈婷婷 饰）是黄芪心中的宝贝；特异独行的魏妍（吴莫愁 饰）则是厚朴求之不得的真爱；唯独辛荑整个大学都“守身如玉”。 青春就在他们一次次荷尔蒙的释放中渐行渐远……', 7, 148000000, '中国大陆', '剧情／爱情', 'https://p0.meituan.net/movie/c0e1d08ccfef4eb97eda8315f116d96f661997.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (241, '狄仁杰之神都龙王', 3, '2013-09-28 08:00:00', 120, '唐朝麟德年间，唐高宗（盛鉴 饰）与武则天（刘嘉玲 饰）强硬派出大军远征，谁知舰队在大海中遇袭，遭到重挫。该事件震惊洛阳，都城百姓狂热祭拜龙王，缭乱众生的“花魁”银睿姬（杨颖 饰）更惹来各方势力觊觎。狄仁杰（赵又廷 饰）奉武后之命调查龙王一案，偏巧卷入绑架银睿姬的案件之中。在与劫匪搏杀过程中，狄仁杰遭遇亦敌亦友的大理寺卿尉迟真金（冯绍峰 饰），遂被对方投入大牢。银睿姬一案内龙王身影再现，使洛阳上空更添一道疑云。狱中狄仁杰凭借缜密推理博取回纥医工沙陀忠（林更新 饰）的信任，逃出大牢同查龙王真相。两起案件错综复杂，相互纠缠，引向全然未知的方向……', 8.3, 601000000, '中国大陆', '动作／悬疑／古装', 'https://p1.meituan.net/movie/58a7b3fc717e87bcf1172950e671a613984346.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (242, '烈日灼心', 3, '2015-08-27 08:00:00', 120, '七年前，福建西陇发生一起惨绝人寰的灭门惨案，某别墅内一家五口同日惨死，在社会上引起极大的震动，然而此去经年，嫌疑人杨自道（郭涛 饰）、辛小丰（邓超 饰）、陈比觉（高虎 饰）却依然逍遥法外。现如今，这三个人都在厦门过活，杨当起了出租车司机，小丰加入了协警队伍，因意外变成傻子的陈则带着三人捡来的女孩尾巴栖息在亲戚的渔场中。这一天，拥有丰富办案经验的伊谷春（段奕宏 饰）调到小丰所在的队伍担任警长。伊颇为器重能力卓越的小丰，但嗅觉灵敏的他也隐隐觉出这个男子和当年的灭门惨案有所关联。在此期间，道哥在一次意外中结识了伊的妹妹小夏（王珞丹 饰）。仿佛冥冥之中有一只看不见的手，正将这三个决心重新做人的男人推向无法更改的结局…… 本片根据须一瓜的长篇小说《太阳黑子》改编。', 8.9, 303000000, '中国大陆', '剧情／犯罪／悬疑', 'https://p0.meituan.net/moviemachine/2ffaceaecd9cfffc364af17e40ae293e579394.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (243, '人在囧途', 3, '2010-06-04 08:00:00', 120, '大老板李成功（徐峥 饰）遇上挤奶工牛耿（王宝强 饰）之后，旅途便频出状况。被情人逼迫回长沙老家跟老婆摊牌的李成功，机场遭遇到前往长沙讨债的乌鸦嘴牛耿。牛耿人如其名，不但耿直憨厚，而且透出一股傻气。先是登机前安检一口气喝完一大罐牛奶，后来又飞机上让乘务员开窗，好不容易折腾到飞机到达长沙上空，结果让他咒得因长沙大雪飞机被迫返航。无奈挤上火车硬座车厢的李成功刚松了一口气，却又一次在人群中看到牛耿。牛耿就像李成功生命中的瘟神一样，只要他“金口一开”，便会出现如他所言的意外。由于途中的频频意外，两人从火车换乘巴士，又从巴士爬上拖拉机。尽管牛耿的乌鸦嘴让李成功吃尽苦头，但这个混身透着傻气的青年却用自己真诚与乐观感染着李成功。一路的颠簸之后，两人最终到达长沙又回到各自的生活轨迹中去，然而旅途中所遭遇的种种却影响着两人之后的生活……', 8.8, 37300000, '中国大陆', '喜剧', 'https://p1.meituan.net/movie/c28bb4f8c944d5ff48592b9a636c8646351261.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (244, '赤道', 3, '2015-04-30 08:00:00', 120, '韩国秘密核武器神秘失踪，同时载着军方手提装置的飞机在中国境内坠毁。韩方接到消息，一名疑似绰号“赤道”的危险人物（张震 饰）带着助手“信差”（文咏珊 饰）将于香港实行秘密武器交易，韩国武器专家崔民号（池珍熙 饰）与特工朴宇哲（崔始源 饰）奉命前往调查。香港反恐特勤组主管李彦 明（张家辉 饰）与督察范家明（余文乐 饰）协助韩方寻找武器，而核物理教授肇志仁（张学友 饰）也受邀协助判案。武器很快被寻回，没想到竟还隐藏着潜在危机。正当香港及韩国警方决定送还武器回韩国之际，大陆安全局高管（王学圻 饰）带着助手袁小姐（冯文娟 饰）突然出现，三方陷入周旋之中，原来“赤道”另有其人，而他们一伙儿的阴谋也开始实施……', 7.3, 209000000, '中国大陆', '动作／悬疑／犯罪', 'https://p1.meituan.net/moviemachine/d574eaa30d90e230cd2b603cc4ef9c3d3369571.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (245, '心迷宫', 3, '2015-10-16 08:00:00', 120, '影片讲述了一个女孩为了拴住恋人假装怀孕造成一桩命案后引发了村中一系列荒诞事件的故事。村长的儿子肖宗耀（王笑天 饰），在一次争执中失手杀死了同村的痞子白虎（朱自清 饰），被迫逃亡。同时，一个饱受家庭暴力摧残的留守女人丽琴（孙黎 饰），在曾经恋人王宝山（邵胜杰 饰）的怀抱里找到了慰藉，两人密谋杀夫。但当丽琴又一次陷入绝望，另一个男人大壮（贾致钢 饰）出现在她的生活里…正直的老村长肖卫国（霍卫民 饰）本期盼着完美的晚年生活，但却因儿子而陷入了人性的纠葛之中。', 8.2, 10760000, '中国大陆', '剧情／悬疑／犯罪', 'https://p0.meituan.net/movie/5ef5b138e2165cecdea7a6eaea140c84642423.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (246, '天机·富春山居图', 3, '2013-06-09 08:00:00', 120, '中国元代名画《富春山居图》合璧展在即，国际黑市开出天价，日本黑帮、英伦大盗闻风而动。身陷不白之冤的国际特工肖锦汉（刘德华 饰）为证清白重出江湖，暗中执行“孙子兵法”计划；临危受命的中国人保高管林雨嫣（张静初 饰），护宝遇一波三折，困于多方势力漩涡；神秘女郎丽莎（林志玲 饰），百变魅惑，却失落真心；幕后冷血黑手小山本（佟大为 饰），嗜血如命，终落败归尘；但各怀绝技的12星座魔女（石天琦、王曼妮 等饰）为小山本作奸犯科，忠心到极致。肖锦汉为夺画上天入地钻沙漠，能否力克强敌，重现昔日特工风采？林雨嫣博弈于西子湖畔，能否与肖锦汉重归旧好？丽莎险些命丧迪拜，能否摆脱魔爪，迎来涅槃？这一场生死对决，这一段恩怨情仇，都随《富春山居图》的安然归国，迎来命定结局。', 4.8, 300000000, '中国大陆', '动作／冒险', 'https://p0.meituan.net/moviemachine/6db11a0c8ceb8835873d337b96379bd67926629.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (247, '西游记之大闹天宫', 3, '2014-01-31 08:00:00', 120, '魔界之王牛魔王（郭富城 饰）与玉帝（周润发 饰）为首的神界展开战斗，神界被毁，女娲（张梓琳 饰）化身晶石修补天庭，遗落下的一小颗晶石落到人间成了一只石猴，而牛魔王则战败被迫带着妻子铁扇公主（陈乔恩 饰）躲到了火焰山。若干年后石猴得到了菩提老祖（海一天 饰）的教导被赐名孙悟空（甄子丹 饰），孙悟空来到花果山自立为王，还抢走了东海龙王（刘桦 饰）的定海神针当武器。同时牛魔王立志东山再起，利用了孙悟空与青梅竹马的小狐狸精（夏梓桐 饰）的感情煽动孙悟空大闹天宫，又串通了二郎神（何润东 饰）一起破坏了南天门，孙悟空势在必得，天庭危在旦夕。', 5.2, 1045000000, '中国大陆', '动作／奇幻', 'https://p0.meituan.net/moviemachine/e7a642f3712fc2f4e94bb3010f6e7b607687187.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (248, '澳门风云', 3, '2014-01-31 08:00:00', 120, '闻名中外，曾担任美国赌场保安总顾问的魔术手石一坚（ 周润发 饰），终回流澳门退休，更宴请各方朋友到来庆祝生日宴。其中好友Benz哥奔驰（ 许绍雄 饰）与他的儿子小冷（ 谢霆锋 饰）以及外甥牛必胜牛牛（ 杜汶泽饰），更特别由香港来到澳门到贺。 翌日， 大陆、 香港以及 澳门三地警方，竟不相约而同找上石一坚，希望他可以打击对付 DOA组织集团的高先生（ 高虎 饰），但坚却以退休为由婉拒了事。而小冷同母异父的哥哥是中国公安安排在高先生的卧底，因为左眼假眼记录了洗黑钱的现场并被杀手知道是内鬼和追杀，他躲在小冷在香港的房子里时，把左眼掏出按在了一个比比熊的一只眼睛里。另一边厢，小冷竟与牛必胜一起找上门，希望坚哥收他俩为徙弟，坚哥虽欣赏小冷，但奈何已经退休，只好拒绝，但看在Benz哥份上，就招待他一夜。谁不知，当晚竟遇上了杀手（ 张晋 饰）， 杀手们不问原因直接追击小冷以及坚哥爱女阿彩（ 童菲 饰）。期间虽有中国公安洛子雯（ 景甜 饰）相救，但阿彩终难逃一劫，被害至失忆。（阿彩失忆为坚哥刻意安排为将假眼送至公安手中） 明查暗访下才知道，幕后的黑手竟是高先生。事情又岂会是无缘无故发生，坚哥与小冷二人联手，加上女公安洛子雯三人，布下 奇谋妙计对付高先生，斗智斗力，誓要查个 水落石出，将他绳之于法。', 8.6, 525000000, '中国大陆', '喜剧／动作', 'https://p0.meituan.net/movie/6ff20d43b937de8a4b3521a347eff66d258341.jpg@218w_300h_1e_1c', 0, 0);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (249, ' 二哥来了怎么办', 2, '2021-06-12 08:40:10', 120, '故事讲述了关于重组家庭里吵闹而又温馨的那些事。杨听风杨听雨是一对兄妹，自从杨妈妈与李叔叔再婚后，五花八门的尴尬大戏便在重组家庭中一直上演。这一天，不速之客李圣突然出现，平静的生活就此乱套……天降2哥？父母偏心？亲哥也倒戈？这一连串的巨变令杨听雨不堪忍受，只想大喊一句：2哥来了怎么办！
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (250, '怒火·重案', 2, '2021-07-30 12:41:56', 120, '重案组布网围剿国际毒枭，突然杀出一组蒙面悍匪“黑吃黑”，更冷血屠杀众警察。重案组督察张崇邦（甄子丹饰）亲睹战友被杀，深入追查发现，悍匪首领竟是昔日战友邱刚敖（谢霆锋饰）。原来敖也曾是警队明日之星，而将敖推向罪恶深渊的人，却正是邦。宿命令二人再次纠缠，一切恩怨张崇邦如何作出了断……', 0, 5675757, '中国大陆', '动作／犯罪', 'https://p0.meituan.net/movie/d54457e82843ea19401bfefd46f3966a2198541.jpg@464w_644h_1e_1c', 12300, 28232);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (251, '我没谈完的那场恋爱', 2, '2021-06-25 19:43:48', 120, '本片讲述了两个千差万别却同样迷茫的年轻音乐人，一个是多年打拼,事业毫无起色,又屡遭生活暴击的和声歌手珊妮,一个是身患重病,隐姓埋名等待死亡降临的前摇滚乐手董东,对生命感到绝望的二人在自我了断时相遇......自杀计划流产,他们的生活也因彼此的出现发生改变。另外一个时空的故事,从珊妮意外发现的一本1970年代的日记中铺陈开来,一段没有下文的悲伤爱情竟然在珊妮的努力下尘埃落定。跟音乐、爱情和人生有关的种种,让珊妮与董东的相遇愈发神奇。千丝万缕中,是否每一种爱都能开花结果? 他们能否找到继续爱这个世界的理由?', 0, 464564, '中国大陆', '剧情／爱情／音乐', 'https://p1.meituan.net/movie/76b8842586867b761de63b42c2d325751523113.jpg@464w_644h_1e_1c', 23900, 74932);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (252, '我的青春有个你', 2, '2021-09-19 20:46:12', 120, '影片改编自小说《草样年华》，大一学渣邱飞（刘冬沁饰）对同级的建筑系学霸周舟（王可如饰）一见钟情，原本瞎混的邱飞找到了上学的意义，那就是和周舟在一起。两条看似不相交的线越走越近。像大部分青春一样，我们总是在不懂爱的时候遇到了最想爱的人。连续不断的意外使两人误会叠生，邱飞也被迫退学，短暂的恋爱无疾而终。十几年后，事业有成的周舟偶然看到了邱飞的摄影作品，她决定去千里之外寻找邱飞……', 0, 123124000, '中国大陆', '青春／爱情', 'https://p1.meituan.net/movie/6f4b650661762b64d2aafb173e670c1c4279787.jpg@464w_644h_1e_1c', 238900, 22453);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (253, '十年一品温如言', 2, '2021-10-01 19:48:17', 120, '除非黄土白骨，守你百岁无忧，你是否遇见十年羁绊百年奉陪的那个人？十年前，寄养在江南水乡的温衡（任敏 饰）第一次被接回北方温家，命运的安排让她遇见了言希（丁禹兮 饰），这一遇见，便是十年纠缠。一个是低眉顺眼的温家女孩，一个是张扬跋扈的言家长孙，他们的人生道路不停交汇又分离，终于发现，两人心中所愿，不过是“永远在一起”。十年后的重逢，伴随着亲朋好友的陪伴与见证，似乎是命运的轮回，给这个“只羡温言不羡仙”的故事画上句点。', 0, 112300, '中国大陆', '剧情／爱情', 'https://p1.meituan.net/moviemachine/e5a853698b1f13be07d1995680b9fda9264471.jpg@464w_644h_1e_1c', 11223, 29956);
INSERT INTO ttms.movie (id, movie_name, movie_status, movie_start, movie_minute, movie_brief, movie_score, movie_money, movie_area, movie_type, movie_head, day_money, want_look) VALUES (254, '寂静之地2', 1, '2021-05-28 20:07:31', 197, '寂静之地2》故事紧承上一部展开。在丈夫为保护家人牺牲后，伊芙琳（艾米莉·布朗特 饰）不得不独自带着孩子们（米莉森·西蒙斯、诺亚·尤佩 饰）面对全新的生存挑战。他们将被迫离开家园，踏上一段未知的旅途。在无声的世界里，除了要躲避“猎声怪物”的捕杀，铺满细沙的道路尽头还暗藏着新的危机。', 7.7, 1200000000, '中国大陆', '惊悚／悬疑／剧情', 'https://p0.meituan.net/moviemachine/4f5ecc2cb68afdfce773914c126cb2dd146582.jpg@464w_644h_1e_1c', 567685, 0);